import { expect } from "chai";
import { SignTypedDataParameters } from "viem";

import { encodeTypedData } from "../src/encodeTypedData";

describe("encodeTypedData", () => {
  it("correctly encodes the domain", () => {
    const [domain] = encodeTypedData(sampleMessage);
    expect(domain).to.equal(
      "",
    );
  });

  it("correctly encodes the message", () => {
    const [, message] = encodeTypedData(sampleMessage);
    expect(message).to.equal(
      "",
    );
  });
});

const sampleMessage = {
  domain: {
    name: "Ether Mail",
    version: "1",
    chainId: 1,
    verifyingContract: "0xCcCCccccCCCCcCCCCCCcCcCccCcCCCcCcccccccC",
  },
  types: {
    Person: [
      { name: "name", type: "string" },
      { name: "wallet", type: "address" },
    ],
    Mail: [
      { name: "from", type: "Person" },
      { name: "to", type: "Person[]" },
      { name: "contents", type: "string" },
    ],
  },
  primaryType: "Mail",
  message: {
    from: {
      name: "Cow",
      wallet: "0xCD2a3d9F938E13CD947Ec05AbC7FE734Df8DD826",
    },
    to: [
      {
        name: "Bob",
        wallet: "0xbBbBBBBbbBBBbbbBbbBbbbbBBbBbbbbBbBbbBBbB",
      },
    ],
    contents: "Hello, Bob!",
  },
} as const satisfies Omit<SignTypedDataParameters, "account">;
